}

export default function ChallengeCreationModal({ onCreateChallenge, children }: ChallengeCreationModalProps) {
  const [open, setOpen] = useState(false);
  const [selectedType, setSelectedType] = useState<typeof CHALLENGE_TYPES[number]>(CHALLENGE_TYPES[0]);
  const [customName, setCustomName] = useState('');
  const [selectedSubjects, setSelectedSubjects] = useState<string[]>([]);
  const [selectedTopics, setSelectedTopics] = useState<Record<string, string[]>>({});
  const [dailyHours, setDailyHours] = useState([4]);
  const [startDate, setStartDate] = useState(format(new Date(), 'yyyy-MM-dd'));

  const handleSubjectToggle = (subject: string) => {
    const newSubjects = selectedSubjects.includes(subject)
      ? selectedSubjects.filter(s => s !== subject)
      : [...selectedSubjects, subject];

    setSelectedSubjects(newSubjects);

    // Remove topics for unselected subjects
    if (!newSubjects.includes(subject)) {
      const newTopics = { ...selectedTopics };
      delete newTopics[subject];
      setSelectedTopics(newTopics);
    }
  };

  const handleTopicToggle = (subject: string, topic: string) => {
    const currentTopics = selectedTopics[subject] || [];
    const newTopics = currentTopics.includes(topic)
      ? currentTopics.filter(t => t !== topic)
      : [...currentTopics, topic];

    setSelectedTopics({
      ...selectedTopics,
      [subject]: newTopics
    });
  };

  const handleCreate = () => {
    console.log('Creating challenge with:', {
      type: selectedType,
      name: customName || selectedType.name,
      subjects: selectedSubjects,
      topics: selectedTopics,
      dailyHours: dailyHours[0],
      startDate
    });

    const challenge: InsertChallenge = {
      type: selectedType.id,
      name: customName || selectedType.name,
      duration: selectedType.duration,
      subjects: selectedSubjects as any,
      topics: selectedTopics,
      startDate,
      endDate: format(addDays(new Date(startDate), selectedType.duration), 'yyyy-MM-dd'),
      dailyTimeHours: dailyHours[0],
      status: 'active'
    };

    onCreateChallenge?.(challenge);
    setOpen(false);
  };

  const isValidChallenge = selectedSubjects.length > 0 && 
    selectedSubjects.every(subject => (selectedTopics[subject] || []).length > 0);

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        {children || (
          <Button data-testid="button-create-challenge">
            <Plus className="h-4 w-4 mr-2" />
            Create Challenge
          </Button>
        )}
      </DialogTrigger>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>Create New JEE Challenge</DialogTitle>
        </DialogHeader>

        <div className="space-y-6">
          {/* Challenge Type Selection */}
          <div className="space-y-3">
            <Label className="text-base font-semibold">Choose Challenge Type</Label>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
              {CHALLENGE_TYPES.map((type) => (
                <Card 
                  key={type.id} 
                  className={`cursor-pointer hover-elevate transition-colors ${
                    selectedType.id === type.id ? 'ring-2 ring-primary' : ''
                  }`}
                  onClick={() => setSelectedType(type)}
                  data-testid={`card-challenge-type-${type.id}`}
                >
                  <CardHeader className="pb-2">
                    <CardTitle className="text-lg">{type.name}</CardTitle>
                    <div className="flex items-center gap-4 text-sm text-muted-foreground">
                      <div className="flex items-center gap-1">
                        <Calendar className="h-4 w-4" />
                        {type.duration} days
                      </div>
                    </div>
                  </CardHeader>
                  <CardContent>
                    <p className="text-sm text-muted-foreground">{type.description}</p>
                  </CardContent>
                </Card>
              ))}
            </div>
          </div>

          {/* Custom Name */}
          <div className="space-y-2">
            <Label htmlFor="challenge-name">Custom Challenge Name (Optional)</Label>
            <Input
              id="challenge-name"
              placeholder={selectedType.name}
              value={customName}
              onChange={(e) => setCustomName(e.target.value)}
              data-testid="input-challenge-name"
            />
          </div>

          {/* Start Date */}
          <div className="space-y-2">
            <Label htmlFor="start-date">Start Date</Label>
            <Input
              id="start-date"
              type="date"
              value={startDate}
              onChange={(e) => setStartDate(e.target.value)}
              data-testid="input-start-date"
            />
          </div>

          {/* Daily Study Hours */}
          <div className="space-y-3">
            <Label className="flex items-center gap-2">
              <Clock className="h-4 w-4" />
              Daily Study Hours: {dailyHours[0]}h
            </Label>
            <Slider
              value={dailyHours}
              onValueChange={setDailyHours}
              max={12}
              min={1}
              step={1}
              className="w-full"
              data-testid="slider-daily-hours"
            />
          </div>

          {/* Subject Selection */}
          <div className="space-y-3">
            <Label className="text-base font-semibold flex items-center gap-2">
              <BookOpen className="h-4 w-4" />
              Select Subjects
            </Label>
            <div className="flex gap-4">
              {JEE_SUBJECTS.map((subject) => (
                <div key={subject} className="flex items-center space-x-2">
                  <Checkbox 
                    id={subject}
                    checked={selectedSubjects.includes(subject)}
                    onCheckedChange={() => handleSubjectToggle(subject)}
                    data-testid={`checkbox-subject-${subject}`}
                  />
                  <Label htmlFor={subject} className="text-sm font-medium">
                    {subject}
                  </Label>
                </div>
              ))}
            </div>
          </div>

          {/* Topic Selection */}
          {selectedSubjects.length > 0 && (
            <div className="space-y-4">
              <Label className="text-base font-semibold">Select Topics</Label>
              {selectedSubjects.map((subject) => {
                const gradeTopics = getTopicsByGradeAndChapter(subject as 'Physics' | 'Chemistry' | 'Mathematics');
                return (
                  <Card key={subject}>
                    <CardHeader className="pb-3">
                      <CardTitle className="text-lg">{subject}</CardTitle>
                    </CardHeader>
                    <CardContent>
                      <Accordion type="multiple" className="w-full">
                        {Object.entries(gradeTopics).map(([grade, chapters]) => {
                          const hasChapters = Object.keys(chapters).length > 0;
                          if (!hasChapters) return null;

                          return (
                            <AccordionItem key={`${subject}-${grade}`} value={`${subject}-${grade}`}>
                              <AccordionTrigger 
                                className="text-base font-medium"
                                data-testid={`accordion-grade-${subject}-${grade}`}
                              >
                                <div className="flex items-center gap-2">
                                  <span>{grade}</span>
                                  <Badge variant="outline" className="text-xs">
                                    {Object.values(chapters).flat().length} topics
                                  </Badge>
                                </div>
                              </AccordionTrigger>
                              <AccordionContent>
                                <div className="space-y-4">
                                  {Object.entries(chapters).map(([chapter, topics]) => (
                                    <div key={`${subject}-${chapter}`} className="space-y-2">
                                      <div className="flex items-center gap-2">
                                        <h4 className="font-medium text-sm text-muted-foreground">{chapter}</h4>
                                        <Badge variant="secondary" className="text-xs">
                                          {topics.length}
                                        </Badge>
                                      </div>
                                      <div className="grid grid-cols-1 md:grid-cols-2 gap-2 ml-4">
                                        {topics.map((topic) => (
                                          <div key={topic} className="flex items-center space-x-2">
                                            <Checkbox 
                                              id={`${subject}-${topic}`}
                                              checked={(selectedTopics[subject] || []).includes(topic)}
                                              onCheckedChange={() => handleTopicToggle(subject, topic)}
                                              data-testid={`checkbox-topic-${subject}-${topic}`}
                                            />
                                            <Label 
                                              htmlFor={`${subject}-${topic}`} 
                                              className="text-sm leading-tight flex items-center gap-1"
                                            >
                                              {topic}
                                              {isCustomTopic(subject as 'Physics' | 'Chemistry' | 'Mathematics', topic) && (
                                                <Badge variant="outline" className="text-xs px-1">
                                                  Custom
                                                </Badge>
                                              )}
                                            </Label>
                                          </div>
                                        ))}
                                      </div>
                                    </div>
                                  ))}
                                </div>
                              </AccordionContent>
                            </AccordionItem>
                          );
                        })}
                      </Accordion>
                    </CardContent>
                  </Card>
                );
              })}
            </div>
          )}

          {/* Summary */}
          {selectedSubjects.length > 0 && (
            <Card className="bg-muted/50">
              <CardHeader>
                <CardTitle className="text-lg">Challenge Summary</CardTitle>
              </CardHeader>
              <CardContent className="space-y-3">
                <div className="grid grid-cols-2 gap-4 text-sm">
                  <div>
                    <span className="font-semibold">Duration:</span> {selectedType.duration} days
                  </div>
                  <div>
                    <span className="font-semibold">Daily Hours:</span> {dailyHours[0]}h
                  </div>
                  <div>
                    <span className="font-semibold">Start Date:</span> {format(new Date(startDate), 'MMM dd, yyyy')}
                  </div>
                  <div>
                    <span className="font-semibold">End Date:</span> {format(addDays(new Date(startDate), selectedType.duration), 'MMM dd, yyyy')}
                  </div>
                </div>
                <div>
                  <span className="font-semibold">Subjects & Topics:</span>
                  <div className="mt-2 space-y-2">
                    {selectedSubjects.map((subject) => (
                      <div key={subject}>
                        <span className="text-sm font-medium">{subject}:</span>
                        <div className="flex flex-wrap gap-1 mt-1">
                          {(selectedTopics[subject] || []).map((topic) => (
                            <Badge key={topic} variant="secondary" className="text-xs">
                              {topic}
                            </Badge>
                          ))}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </CardContent>
            </Card>
          )}

          {/* Action Buttons */}
          <div className="flex justify-end gap-3">
            <Button variant="outline" onClick={() => setOpen(false)} data-testid="button-cancel">
              Cancel
            </Button>
            <Button 
              onClick={handleCreate} 
              disabled={!isValidChallenge}
              data-testid="button-create"
            >
              Create Challenge
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/DailyTaskList.tsx.content:
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Checkbox } from "@/components/ui/checkbox";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Clock, BookOpen, CheckCircle2, Circle, StickyNote } from "lucide-react";
import { DailyTask } from "@shared/schema";
import { format } from "date-fns";

interface DailyTaskListProps {
  tasks: DailyTask[];
  date: string;
  onTaskToggle?: (taskId: string, completed: boolean) => void;
  onAddNote?: (taskId: string, note: string) => void;
}

export default function DailyTaskList({ tasks, date, onTaskToggle, onAddNote }: DailyTaskListProps) {
  const [expandedNotes, setExpandedNotes] = useState<Record<string, boolean>>({});
  const [noteTexts, setNoteTexts] = useState<Record<string, string>>({});

  const completedTasks = tasks.filter(task => task.completed).length;
  const totalTime = tasks.reduce((acc, task) => acc + task.timeAllotted, 0);
  const completedTime = tasks.filter(task => task.completed).reduce((acc, task) => acc + task.timeAllotted, 0);

  const getSubjectColor = (subject: string) => {
    switch (subject) {
      case 'Physics': return 'bg-chart-3 text-white';
      case 'Chemistry': return 'bg-chart-1 text-white';
      case 'Mathematics': return 'bg-chart-2 text-white';
      default: return 'bg-muted text-muted-foreground';
    }
  };

  const getDifficultyColor = (difficulty: string) => {
    switch (difficulty) {
      case 'easy': return 'bg-chart-1 text-white';
      case 'medium': return 'bg-chart-2 text-white';
      case 'hard': return 'bg-destructive text-destructive-foreground';
      default: return 'bg-muted text-muted-foreground';
    }
  };

  const handleTaskToggle = (taskId: string, completed: boolean) => {
    console.log(`Task ${taskId} ${completed ? 'completed' : 'uncompleted'}`);
    onTaskToggle?.(taskId, completed);
  };

  const handleNoteToggle = (taskId: string) => {
    setExpandedNotes(prev => ({
      ...prev,
      [taskId]: !prev[taskId]
    }));
  };

  const handleNoteSave = (taskId: string) => {
    const note = noteTexts[taskId] || '';
    console.log(`Saving note for task ${taskId}:`, note);
    onAddNote?.(taskId, note);
    setExpandedNotes(prev => ({ ...prev, [taskId]: false }));
  };

  return (
    <Card data-testid="daily-task-list">
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center gap-2">
            <BookOpen className="h-5 w-5" />
            Tasks for {format(new Date(date), 'MMMM dd, yyyy')}
          </CardTitle>
          <div className="flex items-center gap-4 text-sm text-muted-foreground">
            <div className="flex items-center gap-1">
              <CheckCircle2 className="h-4 w-4" />
              {completedTasks}/{tasks.length} completed
            </div>
            <div className="flex items-center gap-1">
              <Clock className="h-4 w-4" />
              {Math.round(completedTime / 60)}h/{Math.round(totalTime / 60)}h
            </div>
          </div>
        </div>
      </CardHeader>

      <CardContent className="space-y-3">
        {tasks.length === 0 ? (
          <div className="text-center py-8 text-muted-foreground">
            <Circle className="h-12 w-12 mx-auto mb-2 opacity-50" />
            <p>No tasks scheduled for this day</p>
          </div>
        ) : (
          tasks.map((task, index) => (
            <Card 
              key={task.id} 
              className={`hover-elevate transition-all ${
                task.completed ? 'bg-muted/30' : ''
              }`}
              data-testid={`task-card-${task.id}`}
            >
              <CardContent className="p-4">
                <div className="flex items-start gap-3">
                  <Checkbox 
                    checked={task.completed}
                    onCheckedChange={(checked) => handleTaskToggle(task.id, checked as boolean)}
                    className="mt-1"
                    data-testid={`checkbox-task-${task.id}`}
                  />

                  <div className="flex-1 space-y-2">
                    <div className="flex items-start justify-between gap-2">
                      <div className="space-y-1">
                        <h4 className={`font-medium ${task.completed ? 'line-through text-muted-foreground' : ''}`}>
                          {task.topic}
                        </h4>
                        <p className={`text-sm ${task.completed ? 'line-through text-muted-foreground' : 'text-muted-foreground'}`}>
                          {task.description}
                        </p>
                      </div>

                      <div className="flex flex-col gap-1 text-right">
                        <div className="flex items-center gap-1">
                          <Clock className="h-3 w-3" />
                          <span className="text-xs">{task.timeAllotted}min</span>
                        </div>
                        {task.completedAt && (
                          <span className="text-xs text-chart-1">âœ“ Completed</span>
                        )}
                      </div>
                    </div>

                    <div className="flex items-center gap-2">
                      <Badge className={getSubjectColor(task.subject)}>
                        {task.subject}
                      </Badge>
                      <Badge variant="outline">
                        {task.taskType.replace('-', ' ')}
                      </Badge>
                      <Badge className={getDifficultyColor(task.difficulty)}>
                        {task.difficulty}
                      </Badge>
                    </div>

                    <div className="flex items-center gap-2">
                      <Button 
                        variant="ghost" 
                        size="sm" 
                        onClick={() => handleNoteToggle(task.id)}
                        className="h-6 px-2 text-xs"
                        data-testid={`button-note-${task.id}`}
                      >
                        <StickyNote className="h-3 w-3 mr-1" />
                        {task.notes ? 'Edit Note' : 'Add Note'}
                      </Button>
                      {task.notes && (
                        <span className="text-xs text-muted-foreground truncate max-w-[200px]">
                          "{task.notes}"
                        </span>
                      )}
                    </div>

                    {expandedNotes[task.id] && (
                      <div className="space-y-2 pt-2 border-t">
                        <Textarea 
                          placeholder="Add your notes about this task..."
                          value={noteTexts[task.id] || task.notes || ''}
                          onChange={(e) => setNoteTexts(prev => ({ ...prev, [task.id]: e.target.value }))}
                          className="text-sm"
                          rows={3}
                          data-testid={`textarea-note-${task.id}`}
                        />
                        <div className="flex gap-2">
                          <Button 
                            size="sm" 
                            onClick={() => handleNoteSave(task.id)}
                            data-testid={`button-save-note-${task.id}`}
                          >
                            Save Note
                          </Button>
                          <Button 
                            variant="outline" 
                            size="sm" 
                            onClick={() => handleNoteToggle(task.id)}
                            data-testid={`button-cancel-note-${task.id}`}
                          >
                            Cancel
                          </Button>
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              </CardContent>
            </Card>
          ))
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/DashboardStats.tsx.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { TrendingUp, Target, Calendar, Zap } from "lucide-react";

interface DashboardStatsProps {
  totalChallenges: number;
  activeChallenges: number;
  completedChallenges: number;
  currentStreak: number;
  totalStudyHours: number;
  tasksCompleted: number;
}

export default function DashboardStats({ 
  totalChallenges, 
  activeChallenges, 
  completedChallenges, 
  currentStreak, 
  totalStudyHours, 
  tasksCompleted 
}: DashboardStatsProps) {
  const stats = [
    {
      title: "Active Challenges",
      value: activeChallenges,
      total: totalChallenges,
      icon: Target,
      color: "text-chart-1",
      bgColor: "bg-chart-1/10"
    },
    {
      title: "Completed Challenges",
      value: completedChallenges,
      total: totalChallenges,
      icon: TrendingUp,
      color: "text-chart-1",
      bgColor: "bg-chart-1/10"
    },
    {
      title: "Current Streak",
      value: currentStreak,
      suffix: "days",
      icon: Zap,
      color: "text-chart-2",
      bgColor: "bg-chart-2/10"
    },
    {
      title: "Study Hours",
      value: totalStudyHours,
      suffix: "hrs",
      icon: Calendar,
      color: "text-chart-3",
      bgColor: "bg-chart-3/10"
    }
  ];

  return (
    <div className="grid grid-cols-2 lg:grid-cols-4 gap-4" data-testid="dashboard-stats">
      {stats.map((stat, index) => {
        const Icon = stat.icon;
        return (
          <Card key={stat.title} className="hover-elevate" data-testid={`stat-card-${index}`}>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium text-muted-foreground">
                {stat.title}
              </CardTitle>
              <div className={`p-2 rounded-md ${stat.bgColor}`}>
                <Icon className={`h-4 w-4 ${stat.color}`} />
              </div>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold" data-testid={`stat-value-${index}`}>
                {stat.value}
                {stat.suffix && <span className="text-lg text-muted-foreground ml-1">{stat.suffix}</span>}
              </div>
              {stat.total && (
                <p className="text-xs text-muted-foreground">
                  of {stat.total} total
                </p>
              )}
            </CardContent>
          </Card>
        );
      })}
    </div>
  );
}

client/src/components/StreakCounter.tsx.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Zap, TrendingUp, Calendar } from "lucide-react";
import { motion } from "framer-motion";

interface StreakCounterProps {
  currentStreak: number;
  longestStreak: number;
  totalDays: number;
  studiedToday: boolean;
}

export default function StreakCounter({ currentStreak, longestStreak, totalDays, studiedToday }: StreakCounterProps) {
  const streakPercentage = longestStreak > 0 ? (currentStreak / longestStreak) * 100 : 0;

  return (
    <Card className="hover-elevate" data-testid="streak-counter">
      <CardHeader className="pb-3">
        <CardTitle className="flex items-center gap-2 text-lg">
          <div className={`p-2 rounded-md ${
            studiedToday ? 'bg-chart-2/20' : 'bg-muted'
          }`}>
            <Zap className={`h-5 w-5 ${
              studiedToday ? 'text-chart-2' : 'text-muted-foreground'
            }`} />
          </div>
          Study Streak
          {studiedToday && (
            <Badge className="bg-chart-2 text-white" data-testid="badge-studied-today">
              Today âœ“
            </Badge>
          )}
        </CardTitle>
      </CardHeader>

      <CardContent className="space-y-4">
        <div className="text-center space-y-2">
          <motion.div 
            className="text-4xl font-bold text-chart-2"
            initial={{ scale: 1 }}
            animate={{ scale: studiedToday ? [1, 1.1, 1] : 1 }}
            transition={{ duration: 0.5 }}
            data-testid="text-current-streak"
          >
            {currentStreak}
          </motion.div>
          <p className="text-sm text-muted-foreground">
            {currentStreak === 1 ? 'day' : 'days'} current streak
          </p>
        </div>

        <div className="space-y-3">
          <div className="flex items-center justify-between text-sm">
            <div className="flex items-center gap-2">
              <TrendingUp className="h-4 w-4 text-chart-1" />
              <span>Longest Streak</span>
            </div>
            <span className="font-semibold" data-testid="text-longest-streak">{longestStreak} days</span>
          </div>

          <div className="flex items-center justify-between text-sm">
            <div className="flex items-center gap-2">
              <Calendar className="h-4 w-4 text-chart-3" />
              <span>Total Study Days</span>
            </div>
            <span className="font-semibold" data-testid="text-total-days">{totalDays} days</span>
          </div>
        </div>

        {/* Streak Progress Bar */}
        <div className="space-y-2">
          <div className="flex justify-between text-xs text-muted-foreground">
            <span>Progress to longest</span>
            <span>{Math.round(streakPercentage)}%</span>
          </div>
          <div className="w-full bg-muted rounded-full h-2">
            <motion.div 
              className="bg-chart-2 h-2 rounded-full"
              initial={{ width: 0 }}
              animate={{ width: `${Math.min(streakPercentage, 100)}%` }}
              transition={{ duration: 1, delay: 0.2 }}
              data-testid="progress-streak"
            />
          </div>
        </div>

        {currentStreak === longestStreak && currentStreak > 0 && (
          <motion.div 
            className="text-center p-2 bg-chart-2/10 rounded-md"
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.5 }}
            data-testid="personal-best-badge"
          >
            <p className="text-sm font-medium text-chart-2">ðŸ† Personal Best!</p>
          </motion.div>
        )}

        {!studiedToday && currentStreak > 0 && (
          <motion.div 
            className="text-center p-2 bg-chart-2/10 rounded-md border border-chart-2/20"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.3 }}
            data-testid="motivation-message"
          >
            <p className="text-sm text-chart-2">Don't break the streak! Study today to keep it going.</p>
          </motion.div>
        )}
      </CardContent>
    </Card>
  );
}

